mapper.update:
update users
set password = #{ password }, 
	birthday = #{ birthday },
	phone = #{ phone },
	email = #{ email }
where user_id = #{ user_id }

update users 
set password = #{ password }
where user_id = #{ user_id }

mapper.select:
select count(*) from users
where user_id = #{ user_id }
and email = #{ email }
